<style>
    .center {
        position: absolute;
        left: 50%;
        z-index: -1;
        transform: translate(-50%, 0%);
    }
    .center svg path{
        
        stroke: var(--gray25);
        fill: var(--gray30);
    }
    .drawer p {
        font-size: var(--fontSizeLarge);
        text-align: center;
        z-index: 999999;
        padding: 0 20%;
        line-height: 1.5;
        margin-top: 3rem;
    }

    .close-button {
        position: absolute;
        right: 18%;
        padding: calc(var(--spacingHalf) * 3);
        width: 12rem;
        height: 3rem;
        top: -6%;
        font-size: var(--fontSizeLarge);
        border: none;
        cursor: pointer;
        color: var(--gray80);
        transition: all .1s;
        background-color: var(--gray25);
    }
    
    .close-button:hover {
        background-color: var(--gray30);
    }
    
    .drawer {
        overflow:visible;
        background-image: linear-gradient(to top, var(--gray20), var(--gray25));
        width: 100vw;
        height: 40rem;
        position: fixed;
        bottom: 0%;
        transition: all .7s ease-in-out;
        clip-path: polygon(20% 0%, 80% 0%, 100% 100%, 0% 100%);
        transform: translateY(100%);
    }
    
    .open {
        transform: translateY(0%);
    }

    table {
        margin: 0 auto;
